<?php

$input = '



';

$keypad = [
	[1, 2, 3],
	[4, 5, 6],
	[7, 8, 9],
];

$location = [1,1];

$directions = [
	'U' => [-1,  0],
	'D' => [ 1,  0],
	'R' => [ 0,  1],
	'L' => [ 0, -1],
];

$buttons = explode("\n", $input);

$code = '';

foreach ($buttons as $button) {
	for ($i = 0; $i < strlen($button); $i++) {
		$location = move($location, $directions[$button[$i]]);
	}
	$code .= $keypad[$location[0]][$location[1]];
}

echo('CODE: ' . $code . "\n");



function move($location, $direction) {
	for ($i = 0; $i < count($direction); $i++) {
		$location[$i] += $direction[$i];
		if ($location[$i] == -1) {
			$location[$i] = 0;
		} elseif ($location[$i] == 3) {
			$location[$i] = 2;
		}
	}

	return $location;
}