<?php

$input = '



';

$keypad = [
	['X', 'X', 'X', 'X', 'X', 'X', 'X'],
	['X', 'X', 'X',  1 , 'X', 'X', 'X'],
	['X', 'X',  2 ,  3 ,  4 , 'X', 'X'],
	['X',  5 ,  6 ,  7 ,  8 ,  9 , 'X'],
	['X', 'X', 'A', 'B', 'C', 'X', 'X'],
	['X', 'X', 'X', 'D', 'X', 'X', 'X'],
	['X', 'X', 'X', 'X', 'X', 'X', 'X'],
];

$location = [3,1];

$directions = [
	'U' => [-1,  0],
	'D' => [ 1,  0],
	'R' => [ 0,  1],
	'L' => [ 0, -1],
];

$buttons = explode("\n", $input);

$code = '';

foreach ($buttons as $button) {
	for ($i = 0; $i < strlen($button); $i++) {
		$location = move($location, $directions[$button[$i]]);
	}
	$code .= $keypad[$location[0]][$location[1]];
}

echo('CODE: ' . $code . "\n");



function move($location, $direction) {
	global $keypad;

	$newLocation = [0,0];

	for ($i = 0; $i < count($direction); $i++) {
		$newLocation[$i] = $location[$i] + $direction[$i];
	}

	if ($keypad[$newLocation[0]][$newLocation[1]] == 'X') {
		return $location;
	} else {
		return $newLocation;
	}
}