<?php

//$buffer = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg';
$buffer = '';


for ($i = 0; $i < strlen($buffer); $i++) {
    $chars = [$buffer[$i]];
    for ($j = 1; $j <= 3; $j++) {
        if (!in_array($buffer[$i + $j], $chars)) {
            $chars[] = $buffer[$i + $j];
        } else {
            continue 2;
        }
    }
    // if we get here, there must be a match
    $marker = $i + 4;
    break;
}

echo("First marker is after character " . $marker . "\n");